import pandas as pd# husk at gemme dette script i samme mappe som den originale Testimony.csv fil liggerdf = pd.read_csv("testimonies.csv", keep_default_na=False) #Husk at Navn skal matche hvad der ligger i jeres mappedf = df.sort_values(by='protocol')#Remove all the "?" letters, and input the real letter or even town that geopy gives cords forcolumns_to_clean = ['place_of_birth', 'place_of_residence', 'ghetto']for column in columns_to_clean:    df[column] = df[column].replace('Nagysz?ll?s', 'Nagyszőllős')    df[column] = df[column].replace('Nagysz?ll?s (6 hét)', 'Nagyszőllős')    df[column] = df[column].replace('Nagysz?l?s', 'Nagyszőllős')    df[column] = df[column].replace('K?rösmez?', 'Körösmező')    df[column] = df[column].replace('Fels?veresmart', 'felsőveresmart')    df[column] = df[column].replace('Fels?bisztra', 'Felsőbisztra') #cant find geopy loc maybe katlanfalu instead    df[column] = df[column].replace('Fels?szopor', 'Felsőszopor')    df[column] = df[column].replace('Fels?visó', 'Felsővisó')    df[column] = df[column].replace('Fels?ripnyice', 'Felsőripnyice') #not sure that geopy can find this loaction, and couldn't find it on maps etc.    df[column] = df[column].replace('Keselymez?', 'Keselymező')    df[column] = df[column].replace('Técs?', 'Técső')    df[column] = df[column].replace('T?cs', 'Tőcs') # I think geopy finds a french town    df[column] = df[column].replace('Csörg?', 'Csörgő')    df[column] = df[column].replace('Turjaporosk?', 'Poroskő') # Turja is a river - the village is called Poroskő it seems    df[column] = df[column].replace('Pestszentl?rincz', 'Pestszentlőrinc')    df[column] = df[column].replace('Pestszentl?rinc', 'Pestszentlőrinc')    df[column] = df[column].replace('Mez?kövesd', 'Mezőkövesd')    # change Munkács outliers ie. 'Munkácsi téglagyár' to Munkács    df.loc[df['ghetto'].str.startswith('Munkács'), 'ghetto'] = 'Munkács'    # Change Auschwitz - "randomTown" to just Auschwitz    df.loc[df['camps'].str.startswith('Auschwitz -'), 'camps'] = 'Auschwitz'print(df)df.to_csv('Cleaned testimonies.csv', index=False, encoding='utf-8') # Creating CSV