import csv import pandas  with open('testimonies.csv') as file_obj:     geoPlots = []    invalidPlots = []    reader_obj = csv.DictReader(file_obj)     for row in reader_obj:          ghetto = row['ghetto'].split(',')         camps = row['camps'].split(',')         pob = row['place_of_birth'].split(',')         for name in ghetto:             if not name == "null":                 if not name.strip().lower() in geoPlots:                     geoPlots.append(name.strip().lower())         for name in camps:            if not name == "null":                if not name.strip().lower() in geoPlots:                    geoPlots.append(name.strip().lower())         for name in pob:            if not name == "null":                if not name.strip().lower() in geoPlots:                    geoPlots.append(name.strip().lower())    for plot in geoPlots:        if '?' in plot:            invalidPlots.append(plot)                    # Creating separate DataFrames for geoPlots and invalidPlotsdf_geo = pandas.DataFrame({'geoplot': geoPlots})df_invalid = pandas.DataFrame({'invalidPlot': invalidPlots})# Combining both DataFramescombined_df = pandas.concat([df_geo, df_invalid], axis=1)combined_df.to_csv('rawplots.csv', index=True, encoding='utf-8')  # Creating CSV                