import csv def obtainPlots():    with open('rawplots.csv') as file_obj:        invalidPlots = []        reader_obj = csv.DictReader(file_obj)        for row in reader_obj:            if row['invalidPlot']:                # if row['invalidPlot'].find('(') >= 0:                #     replaceString = row['invalidPlot'][row['invalidPlot'].find('('):row['invalidPlot'].find(')')+1]                #     invalidPlots.append(row['invalidPlot'].replace(replaceString,''))                # else:                invalidPlots.append(row['invalidPlot'])    return invalidPlotswith open('testimonies.csv') as testimonies:    reader_testimonies = csv.DictReader(testimonies)        invalidPlots = obtainPlots()        placeFound = []    entriesFound = []        for plot in invalidPlots:        testimonies.seek(0)  # Resetting the file pointer to start from the beginning for each plot                for row in reader_testimonies:            if (row['place_of_birth'].lower() == plot.lower() or                 plot.lower() in row['ghetto'].lower().split(',') or                 plot.lower() in row['camps'].lower().split(',')) and plot.lower() not in placeFound and row['protocol'] not in entriesFound:                                placeFound.append(plot.lower())                entriesFound.append(row['protocol'])                break  # Exit the inner loop after finding a match for the current plot    print(placeFound, entriesFound)